/**
 * Rewardpoints Adminhtml Controller
 * 
 * @category    Magestore
 * @package     Magestore_RewardPoints
 * @author      Magestore Developer
 */
class Magestore_RewardPoints_Adminhtml_Reward_SimiController extends Mage_Adminhtml_Controller_Action
{

    /**
     * @return mixed
     */
    protected function _isAllowed()
    {
        return Mage::getSingleton('admin/session')->isAllowed('rewardpoints/simicart');
    }
    
    public function indexAction(){
		$url = "https://www.simicart.com/usermanagement/checkout/buyProfessional/?extension=1&utm_source=magestorebuyer&utm_medium=backend&utm_campaign=Magestore Buyer Backend";
		Mage::app()->getResponse()->setRedirect($url)->sendResponse();
	}
}
